mapper.select:
select car_caracteriza.car_id_secue, ind_indicador.ind_nombre, ind_indicador.ind_indicador, est_estudiantes.est_nombre, est_estudiantes.est_codigo, car_nro_periodo, car_fch_inicial, car_valor_periodo, car_valor_historia, car_agno, car_nro_per_prog, car_valor_promedio from car_caracteriza, ind_indicador, est_estudiantes
car_caracteriza.ind_id_secue = ind_indicador.ind_id_secue and car_caracteriza.est_id_secue = est_estudiantes.est_id_secue
and est_estudiantes.est_codigo = #{estudianteCodigo}
and ind_indicador.ind_indicador = #{indicadorCodigo}
and car_caracteriza.car_agno = #{agno}
and car_caracteriza.car_nro_per_prog = #{nroPeriodoProg}
and car_caracteriza.car_nro_periodo = #{nroPeriodo}
order by car_caracteriza.car_agno, car_caracteriza.car_nro_periodo, ind_indicador.ind_indicador, est_estudiantes.est_nombre asc limit 500
