mapper.select:
SELECT IND_ID_SECUE, IND_NOMBRE, IND_DESCRIPCION, IND_TIPO_PERIODO, IND_ESTADO, IND_COBERTURA, IND_TIPO_INDICADOR, IND_INDICADOR, IND_CODEQUIVA FROM ind_indicador
IND_ID_SECUE = IND_ID_SECUE
and IND_INDICADOR = #{codigo}
and IND_NOMBRE = #{nombre}
and IND_TIPO_INDICADOR = #{tipoIndicador}
and ind_estado = #{estado}
and IND_TIPO_PERIODO = #{tipoPeriodo}
order by IND_NOMBRE limit 500
