mapper.select:

select  mod_secue,
        mod_nombre,
        mod_descripcion,
        mod_proceso,
        mod_estado
from mod_modelo

   mod_secue = mod_secue

        and mod_nombre = #{mod_nombre}


        and mod_estado = #{mod_estado}


order by mod_nombre limit 500   ;

mapper.insert:
insert into isahc.mod_modelo
( mod_nombre,
mod_descripcion,
mod_proceso,
mod_estado
)
values
( #{mod_nombre},
  #{mod_descripcion},
  #{mod_proceso},
  #{mod_estado}
);
